  { alt_statement_notation: "2.NBT.6", asn_identifier: "S114345C", description: "Add up to four two-digit numbers using strategies based on place value and properties of operations.", grade: "Grade 2", standard_id: "70CC6A0456AB4CD1A86BC8EA43B447BA", statement_notation: "CCSS.Math.Content.2.NBT.B.6", subject: "Common Core Mathematics" },
  { alt_statement_notation: "2.NBT.5", asn_identifier: "S114345B", description: "Fluently add and subtract within 100 using strategies based on place value, properties of operations, and/or the relationship between addition and subtraction.", grade: "Grade 2", standard_id: "7C9ACFC70C934A229B447804D6A1C0FC", statement_notation: "CCSS.Math.Content.2.NBT.B.5", subject: "Common Core Mathematics" },
