  { alt_statement_notation: "2.NBT.1.b", asn_identifier: "S2373204", description: "The numbers 100, 200, 300, 400, 500, 600, 700, 800, 900 refer to one, two, three, four, five, six, seven, eight, or nine hundreds (and 0 tens and 0 ones).", grade: "Grade 2", standard_id: "0544B12300304F739F00C96785DCE7D2", statement_notation: "CCSS.Math.Content.2.NBT.A.1b", subject: "Common Core Mathematics" },
  { alt_statement_notation: "2.NBT.1.a", asn_identifier: "S2373203", description: "100 can be thought of as a bundle of ten tens — called a \"hundred.\"", grade: "Grade 2", standard_id: "C9623D25BEB947A38CA8A92AB009E699", statement_notation: "CCSS.Math.Content.2.NBT.A.1a", subject: "Common Core Mathematics" },
  { alt_statement_notation: "2.NBT.1", asn_identifier: "S1143457", description: "Understand that the three digits of a three-digit number represent amounts of hundreds, tens, and ones; e.g., 706 equals 7 hundreds, 0 tens, and 6 ones. Understand the following as special cases:", grade: "Grade 2", standard_id: "3B25AF48C22D4668A6085998F847B56E", statement_notation: "CCSS.Math.Content.2.NBT.A.1", subject: "Common Core Mathematics" },
