  { alt_statement_notation: "2.OA.4", asn_identifier: "S1143456", description: "Use addition to find the total number of objects arranged in rectangular arrays with up to 5 rows and up to 5 columns; write an equation to express the total as a sum of equal addends.", grade: "Grade 2", standard_id: "A4531EC480FA4835AF9E3F9348FC5EC1", statement_notation: "CCSS.Math.Content.2.OA.C.4", subject: "Common Core Mathematics" },
  { alt_statement_notation: "2.OA.3", asn_identifier: "S1143455", description: "Determine whether a group of objects (up to 20) has an odd or even number of members, e.g., by pairing objects or counting them by 2s; write an equation to express an even number as a sum of two equal addends.", grade: "Grade 2", standard_id: "C7ECD7455B7D46E89CF07EB8C0A2337A", statement_notation: "CCSS.Math.Content.2.OA.C.3", subject: "Common Core Mathematics" },
