  { alt_statement_notation: "2.OA.2", asn_identifier: "S1143454", description: "Fluently add and subtract within 20 using mental strategies. By end of Grade 2, know from memory all sums of two one-digit numbers.", grade: "Grade 2", standard_id: "800715566B824BB3A5A8C464E961C2B4", statement_notation: "CCSS.Math.Content.2.OA.B.2", subject: "Common Core Mathematics" },
