  { alt_statement_notation: "2.OA.1", asn_identifier: "S1143453", description: "Use addition and subtraction within 100 to solve one- and two-step word problems involving situations of adding to, taking from, putting together, taking apart, and comparing, with unknowns in all positions, e.g., by using drawings and equations with a symbol for the unknown number to represent the problem.", grade: "Grade 2", standard_id: "800715566B824BB3A5A8C464E961C2B3", statement_notation: "CCSS.Math.Content.2.OA.A.1", subject: "Common Core Mathematics" },
  { alt_statement_notation: nil, asn_identifier: "S2390245", description: "Represent and solve problems involving addition and subtraction.", grade: "Grade 2", standard_id: "25F1A2E4A27442D886723ADD7B62C09E", statement_notation: "CCSS.Math.Content.2.OA.A", subject: "Common Core Mathematics" },
