  { alt_statement_notation: "3.NF.3.d", asn_identifier: "S1143694", description: "Compare two fractions with the same numerator or the same denominator by reasoning about their size. Recognize that comparisons are valid only when the two fractions refer to the same whole. Record the results of comparisons with the symbols >, =, or <, and justify the conclusions, e.g., by using a visual fraction model.", grade: "Grade 3", standard_id: "43F9FB0B127B45738F173195B04CAF6B", statement_notation: "CCSS.Math.Content.3.NF.A.3d", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.3.c", asn_identifier: "S1143693", description: "Express whole numbers as fractions, and recognize fractions that are equivalent to whole numbers.", grade: "Grade 3", standard_id: "70C12332BEED45098AA0A0BBCD977376", statement_notation: "CCSS.Math.Content.3.NF.A.3c", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.3.b", asn_identifier: "S1143692", description: "Recognize and generate simple equivalent fractions, e.g., 1/2 = 2/4, 4/6 = 2/3). Explain why the fractions are equivalent, e.g., by using a visual fraction model.", grade: "Grade 3", standard_id: "8A6F987095B24FD8825585A2510C4356", statement_notation: "CCSS.Math.Content.3.NF.A.3b", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.3.a", asn_identifier: "S1143691", description: "Understand two fractions as equivalent (equal) if they are the same size, or the same point on a number line.", grade: "Grade 3", standard_id: "F80BD4BFFDB946DCB74AFA807DF07F0C", statement_notation: "CCSS.Math.Content.3.NF.A.3a", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.3", asn_identifier: "S1143485", description: "Explain equivalence of fractions in special cases, and compare fractions by reasoning about their size.", grade: "Grade 3", standard_id: "95A47E3EB7134F7FA65667B692D6F23E", statement_notation: "CCSS.Math.Content.3.NF.A.3", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.2.b", asn_identifier: "S1143690", description: "Represent a fraction a/b on a number line diagram by marking off a lengths 1/b from 0. Recognize that the resulting interval has size a/b and that its endpoint locates the number a/b on the number line.", grade: "Grade 3", standard_id: "D31E57DF888541EAA539B9C1954C76E1", statement_notation: "CCSS.Math.Content.3.NF.A.2b", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.2.a", asn_identifier: "S114368F", description: "Represent a fraction 1/b on a number line diagram by defining the interval from 0 to 1 as the whole and partitioning it into b equal parts. Recognize that each part has size 1/b and that the endpoint of the part based at 0 locates the number 1/b on the number line.", grade: "Grade 3", standard_id: "74D0C6FACE7E4939BD8B01C5E729871C", statement_notation: "CCSS.Math.Content.3.NF.A.2a", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.2", asn_identifier: "S1143484", description: "Understand a fraction as a number on the number line; represent fractions on a number line diagram.", grade: "Grade 3", standard_id: "8C551F4EF2224A61AED96E27F7BB8852", statement_notation: "CCSS.Math.Content.3.NF.A.2", subject: "Common Core Mathematics" },
  { alt_statement_notation: "3.NF.1", asn_identifier: "S1143483", description: "Understand a fraction 1/b as the quantity formed by 1 part when a whole is partitioned into b equal parts; understand a fraction a/b as the quantity formed by a parts of size 1/b.", grade: "Grade 3", standard_id: "EFC133CD8D34442CA35311157F372C97", statement_notation: "CCSS.Math.Content.3.NF.A.1", subject: "Common Core Mathematics" },
  { alt_statement_notation: nil, asn_identifier: "S1143473", description: "Develop understanding of fractions as numbers.", grade: "Grade 3", standard_id: "FB0C3EF6E0254112BE3E1A3B3BBE691C", statement_notation: "CCSS.Math.Content.3.NF.A", subject: "Common Core Mathematics" },
