  { alt_statement_notation: "3.NBT.3", asn_identifier: "S1143482", description: "Multiply one-digit whole numbers by multiples of 10 in the range 10—90 (e.g., 9 × 80, 5 × 60) using strategies based on place value and properties of operations.", grade: "Grade 3", standard_id: "4056EC7D109E419FB1499E54CE352507", statement_notation: "CCSS.Math.Content.3.NBT.A.3", subject: "Common Core Mathematics" },
