  { alt_statement_notation: "3.NBT.1", asn_identifier: "S1143480", description: "Use place value understanding to round whole numbers to the nearest 10 or 100.", grade: "Grade 3", standard_id: "8B79312AC5B747249C96D19427CE45E3", statement_notation: "CCSS.Math.Content.3.NBT.A.1", subject: "Common Core Mathematics" },
  { alt_statement_notation: nil, asn_identifier: "S1143471", description: "Use place value understanding and properties of operations to perform multi-digit arithmetic.", grade: "Grade 3", standard_id: "600ED56CB17B45BCA84F742EF6CE451C", statement_notation: "CCSS.Math.Content.3.NBT.A", subject: "Common Core Mathematics" },
