  { alt_statement_notation: "3.OA.8", asn_identifier: "S114347E", description: "Solve two-step word problems using the four operations. Represent these problems using equations with a letter standing for the unknown quantity. Assess the reasonableness of answers using mental computation and estimation strategies including rounding.", grade: "Grade 3", standard_id: "3D384CB2349B41299A3B5A133AB9E3F9", statement_notation: "CCSS.Math.Content.3.OA.D.8", subject: "Common Core Mathematics" },
