  { alt_statement_notation: "4.NF.7", asn_identifier: "S11434AB", description: "Compare two decimals to hundredths by reasoning about their size. Recognize that comparisons are valid only when the two decimals refer to the same whole. Record the results of comparisons with the symbols >, =, or <, and justify the conclusions, e.g., by using a visual model.", grade: "Grade 4", standard_id: "C374A9ECF108450BB9790DDB560BBB27", statement_notation: "CCSS.Math.Content.4.NF.C.7", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.6", asn_identifier: "S11434AA", description: "Use decimal notation for fractions with denominators 10 or 100.", grade: "Grade 4", standard_id: "C374A9ECF108450BB9790DDB560BBB26", statement_notation: "CCSS.Math.Content.4.NF.C.6", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.5", asn_identifier: "S11434A9", description: "Express a fraction with denominator 10 as an equivalent fraction with denominator 100, and use this technique to add two fractions with respective denominators 10 and 100.", grade: "Grade 4", standard_id: "C374A9ECF108450BB9790DDB560BBB25", statement_notation: "CCSS.Math.Content.4.NF.C.5", subject: "Common Core Mathematics" },
