  { alt_statement_notation: "4.NF.4.c", asn_identifier: "S114368A", description: "Solve word problems involving multiplication of a fraction by a whole number, e.g., by using visual fraction models and equations to represent the problem.", grade: "Grade 4", standard_id: "697F85A12F954712923BD35862DEC2BB", statement_notation: "CCSS.Math.Content.4.NF.B.4c", subject: "Common Core Mathematics" },
