  { alt_statement_notation: "4.NF.4.a", asn_identifier: "S1143688", description: "Understand a fraction a/b as a multiple of 1/b.", grade: "Grade 4", standard_id: "5AA07FB2E9934DCAB59F4AE0D1624C8B", statement_notation: "CCSS.Math.Content.4.NF.B.4a", subject: "Common Core Mathematics" },
