  { alt_statement_notation: "4.NF.3.d", asn_identifier: "S114368E", description: "Solve word problems involving addition and subtraction of fractions referring to the same whole and having like denominators, e.g., by using visual fraction models and equations to represent the problem.", grade: "Grade 4", standard_id: "863D0723B4434959A7B9449A41488937", statement_notation: "CCSS.Math.Content.4.NF.B.3d", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.3.c", asn_identifier: "S114368D", description: "Add and subtract mixed numbers with like denominators, e.g., by replacing each mixed number with an equivalent fraction, and/or by using properties of operations and the relationship between addition and subtraction.", grade: "Grade 4", standard_id: "3E844B9ECC954ACAA0E0177CE452D4C1", statement_notation: "CCSS.Math.Content.4.NF.B.3c", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.3.b", asn_identifier: "S114368C", description: "Decompose a fraction into a sum of fractions with the same denominator in more than one way, recording each decomposition by an equation. Justify decompositions, e.g., by using a visual fraction model.", grade: "Grade 4", standard_id: "82832D1795064E29BA1CD2CCE62FC28F", statement_notation: "CCSS.Math.Content.4.NF.B.3b", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.3.a", asn_identifier: "S114368B", description: "Understand addition and subtraction of fractions as joining and separating parts referring to the same whole.", grade: "Grade 4", standard_id: "F6933013AE4F438BB52517F900140E51", statement_notation: "CCSS.Math.Content.4.NF.B.3a", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.3", asn_identifier: "S11434A7", description: "Understand a fraction a/b with a > 1 as a sum of fractions 1/b.", grade: "Grade 4", standard_id: "1195E43626BF4A6B8AA96AF62AFB4283", statement_notation: "CCSS.Math.Content.4.NF.B.3", subject: "Common Core Mathematics" },
  { alt_statement_notation: nil, asn_identifier: "S1143495", description: "Build fractions from unit fractions by applying and extending previous understandings of operations on whole numbers.", grade: "Grade 4", standard_id: "CAF90B8CE96A4458829E7C7C376A7126", statement_notation: "CCSS.Math.Content.4.NF.B", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.2", asn_identifier: "S11434A6", description: "Compare two fractions with different numerators and different denominators, e.g., by creating common denominators or numerators, or by comparing to a benchmark fraction such as 1/2. Recognize that comparisons are valid only when the two fractions refer to the same whole. Record the results of comparisons with symbols >, =, or <, and justify the conclusions, e.g., by using a visual fraction model.", grade: "Grade 4", standard_id: "72F5C564F8744643B0876628789CFBC4", statement_notation: "CCSS.Math.Content.4.NF.A.2", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.NF.1", asn_identifier: "S11434A5", description: "Explain why a fraction a/b is equivalent to a fraction (n × a)/(n × b) by using visual fraction models, with attention to how the number and size of the parts differ even though the two fractions themselves are the same size. Use this principle to recognize and generate equivalent fractions.", grade: "Grade 4", standard_id: "138DC8D66B4545BB92DA44D6C78205DD", statement_notation: "CCSS.Math.Content.4.NF.A.1", subject: "Common Core Mathematics" },
