  { alt_statement_notation: "4.G.3", asn_identifier: "S11434B5", description: "Recognize a line of symmetry for a two-dimensional figure as a line across the figure such that the figure can be folded along the line into matching parts. Identify line-symmetric figures and draw lines of symmetry.", grade: "Grade 4", standard_id: "B6CC34045B7B45F48CE0080E44852EEA", statement_notation: "CCSS.Math.Content.4.G.A.3", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.G.2", asn_identifier: "S11434B4", description: "Classify two-dimensional figures based on the presence or absence of parallel or perpendicular lines, or the presence or absence of angles of a specified size. Recognize right triangles as a category, and identify right triangles.", grade: "Grade 4", standard_id: "F2457F2D8F574726BB094F391822B030", statement_notation: "CCSS.Math.Content.4.G.A.2", subject: "Common Core Mathematics" },
