  { alt_statement_notation: nil, asn_identifier: "S1143499", description: "Draw and identify lines and angles, and classify shapes by properties of their lines and angles.", grade: "Grade 4", standard_id: "0B670B7BA80B457D82D11DD3D54DCE92", statement_notation: "CCSS.Math.Content.4.G.A", subject: "Common Core Mathematics" },
