  { alt_statement_notation: "4.MD.3", asn_identifier: "S11434AE", description: "Apply the area and perimeter formulas for rectangles in real world and mathematical problems.", grade: "Grade 4", standard_id: "A19298FADD5945B29D8547168CC4783C", statement_notation: "CCSS.Math.Content.4.MD.A.3", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.MD.2", asn_identifier: "S11434AD", description: "Use the four operations to solve word problems involving distances, intervals of time, liquid volumes, masses of objects, and money, including problems involving simple fractions or decimals, and problems that require expressing measurements given in a larger unit in terms of a smaller unit. Represent measurement quantities using diagrams such as number line diagrams that feature a measurement scale.", grade: "Grade 4", standard_id: "5C63D948361B4814BFF237D6F97ED6E1", statement_notation: "CCSS.Math.Content.4.MD.A.2", subject: "Common Core Mathematics" },
  { alt_statement_notation: "4.MD.1", asn_identifier: "S11434AC", description: "Know relative sizes of measurement units within one system of units including km, m, cm; kg, g; lb, oz.; l, ml; hr, min, sec. Within a single system of measurement, express measurements in a larger unit in terms of a smaller unit. Record measurement equivalents in a two column table.", grade: "Grade 4", standard_id: "D6E6BDF894CE43D89B074BF35BEB24A7", statement_notation: "CCSS.Math.Content.4.MD.A.1", subject: "Common Core Mathematics" },
  { alt_statement_notation: nil, asn_identifier: "S1143497", description: "Solve problems involving measurement and conversion of measurements from a larger unit to a smaller unit.", grade: "Grade 4", standard_id: "618C4BCCE83C4945B4B5F627C87798FE", statement_notation: "CCSS.Math.Content.4.MD.A", subject: "Common Core Mathematics" },
