  { alt_statement_notation: "4.OA.3", asn_identifier: "S114349C", description: "Solve multistep word problems posed with whole numbers and having whole-number answers using the four operations, including problems in which remainders must be interpreted. Represent these problems using equations with a letter standing for the unknown quantity. Assess the reasonableness of answers using mental computation and estimation strategies including rounding.", grade: "Grade 4", standard_id: "D3852439BE5747BA9E52B55237414DBC", statement_notation: "CCSS.Math.Content.4.OA.A.3", subject: "Common Core Mathematics" },
